import React, { Fragment, useState } from 'react';
import axios from 'axios';

const todo = props => {
    const [todoName, setTodoName] = useState('');
    const [todoList, setTodoList] = useState([]);
    // const [todoState, setTodoState] = useState({userInput: '', todoList: []})

    const inputChangeHandler = event => {
        setTodoName(event.target.value);
        // setTodoState({userInput: event.target.value, todoList: todoState.todoList})
    }

    const todoAddHandler = () => {
        setTodoList(todoList.concat(todoName));
        axios.post('https://hooks-testing.firebaseio.com/todaos.json', {name: todoName})
            .then(res => {
                console.log(res);
            })
            .catch(err => {
                console.log(err);
            })
        // setTodoState({userInput: todoState.userInput, todoList: todoState.todoList.concat(todoState.userInput)});
    }

    return (
        <Fragment>
            <input 
            type='text' 
            placeholder='todo' 
            onChange={inputChangeHandler} 
            value={todoName} 
            />
            <button type='button' onClick={todoAddHandler}>Add</button>
            <ul>
                {todoList.map((todo, i) => (<li key={i}>{todo}</li>))}
            </ul>
        </Fragment>
    )
}

export default todo;
